from nltk.corpus import stopwords
import nltk
from Sastrawi.StopWordRemover.StopWordRemoverFactory import StopWordRemoverFactory

OPENROUTER_API_KEY = "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"
OPENROUTER_URL = "https://openrouter.ai/api/v1/chat/completions"
MODEL_NAME = "meta-llama/llama-4-scout"

nltk.download('stopwords')
stopwords_id = set(StopWordRemoverFactory().get_stop_words())
stopwords_en = set(stopwords.words('english'))
stopwords_all = stopwords_id.union(stopwords_en)

def build_prompt(text: str) -> str:
    return f"""
Saya ingin Anda menganalisis sentimen dari paragraf berikut.
Jawaban **WAJIB** hanya dalam format JSON valid seperti ini:
{{
  "sentiment": "positif | netral | negatif",
  "reason": "alasan singkat dalam bahasa Indonesia"
}}

Tanpa tambahan kata apapun.

Paragraf:
\"\"\"{text}\"\"\"
"""

def call_openrouter_api(prompt: str) -> str:
    headers = {
        "Authorization": f"Bearer {OPENROUTER_API_KEY}",
        "Content-Type": "application/json"
    }
    payload = {
        "model": MODEL_NAME,
        "messages": [{"role": "user", "content": prompt}],
        "max_tokens": 150,
        "temperature": 0.3
    }
    try:
        response = requests.post(OPENROUTER_URL, headers=headers, json=payload)
        response.raise_for_status()

        # Ambil isi sebenarnya dari dalam choices -> message -> content
        data = response.json()
        return data["choices"][0]["message"]["content"]

    except requests.RequestException as e:
        logging.error(f" Error API call: {e}")
        return None
    except (KeyError, IndexError) as e:
        logging.error(f" Error parsing response structure: {e}")
        return None


def clean_text(text):
    text = re.sub(r"http\S+", "", text)
    text = re.sub(r"[^a-zA-Z\s]", "", text)
    text = text.lower()
    return text

def safe_parse_json(content: str) -> dict:
    try:
        json_blocks = re.findall(r'\{.*?\}', content, re.DOTALL)

        if not json_blocks:
            raise ValueError(" Tidak ditemukan blok JSON dalam response.")

        for block in json_blocks:
            try:
                return json.loads(block)
            except json.JSONDecodeError:
                continue

        raise ValueError(" Semua blok JSON gagal di-parse.")

    except Exception as e:
        raise ValueError(f" Gagal parse JSON: {e}")


def analyze_sentiment_manual(berita_list):
    results = []

    for i, berita in enumerate(berita_list, start=1):
        combined = f"{berita['judul']}. {berita['isi']}"
        cleaned = clean_text(combined)
        prompt = build_prompt(cleaned)

        try:
            content = call_openrouter_api(prompt)
            if content is None:
                raise ValueError("Response kosong.")

            #print(f"\n== DEBUG RESPONSE untuk berita {i} ==\n{content}\n")  # Untuk debug jika error parsing

            result = safe_parse_json(content)

            sentiment = result.get("sentiment")
            reason = result.get("reason")

            if not sentiment or not reason:
                raise ValueError("Field sentiment/reason tidak ditemukan.")

            results.append({
                "judul": berita['judul'],
                "isi": berita['isi'],
                "sentiment": sentiment,
                "reason": reason
            })

        except Exception as e:
            logging.warning(f" Error parsing berita ke-{i}: {e}")
            results.append({
                "judul": berita['judul'],
                "isi": berita['isi'],
                "sentiment": "netral",
                "reason": "Parsing gagal atau error API"
            })

    return results

